<span class="line">plt<span class="token punctuation">.</span>plot<span class="token punctuation">(</span>train_sizes<span class="token punctuation">,</span> test_mean<span class="token punctuation">,</span> <span class="token string">&#39;o-&#39;</span><span class="token punctuation">,</span> color<span class="token operator">=</span><span class="token string">&quot;g&quot;</span><span class="token punctuation">,</span> label<span class="token operator">=</span><span class="token string">&quot;验证集MSE&quot;</span><span class="token punctuation">)</span></span>
