<span class="line">r2_means <span class="token operator">=</span> <span class="token punctuation">[</span>results<span class="token punctuation">[</span>name<span class="token punctuation">]</span><span class="token punctuation">[</span><span class="token string">&#39;R²&#39;</span><span class="token punctuation">]</span><span class="token punctuation">[</span><span class="token string">&#39;mean&#39;</span><span class="token punctuation">]</span> <span class="token keyword">for</span> name <span class="token keyword">in</span> model_names<span class="token punctuation">]</span></span>
