<span class="line">ax3<span class="token punctuation">.</span>bar<span class="token punctuation">(</span>x<span class="token punctuation">,</span> r2_means<span class="token punctuation">,</span> width<span class="token punctuation">,</span> yerr<span class="token operator">=</span>r2_stds<span class="token punctuation">,</span> label<span class="token operator">=</span><span class="token string">&#39;R²&#39;</span><span class="token punctuation">,</span> color<span class="token operator">=</span><span class="token string">&#39;green&#39;</span><span class="token punctuation">,</span> alpha<span class="token operator">=</span><span class="token number">0.7</span><span class="token punctuation">)</span></span>
